
# This script creates the test files for the project
DATADIR="./data"
TESTSDIR=$DATADIR"/TestFiles"
ENCODEDDIR=$DATADIR"/EncodedFiles"
ThreadsDIR=$ENCODEDDIR"/Threads"
AsyncDIR=$ENCODEDDIR"/Async"
ThreadPooDIR=$ENCODEDDIR"/ThreadPool"
FastFlowDIR=$ENCODEDDIR"/FastFlow"
SequentialDIR=$ENCODEDDIR"/Sequential"

# If not already present, create the data directory
if [ ! -d "$DATADIR" ]; then
    mkdir "$DATADIR"
fi

# If not already present, create the tests directory
if [ ! -d "$TESTSDIR" ]; then
    mkdir "$TESTSDIR"
fi

# If not already present, create the encoded directory
if [ ! -d "$ENCODEDDIR" ]; then
    mkdir "$ENCODEDDIR"
fi

# If not already present, create the threads directory
if [ ! -d "$ThreadsDIR" ]; then
    mkdir "$ThreadsDIR"
fi

# If not already present, create the async directory
if [ ! -d "$AsyncDIR" ]; then
    mkdir "$AsyncDIR"
fi

# If not already present, create the threadpool directory
if [ ! -d "$ThreadPooDIR" ]; then
    mkdir "$ThreadPooDIR"
fi

# If not already present, create the fastflow directory
if [ ! -d "$FastFlowDIR" ]; then
    mkdir "$FastFlowDIR"
fi

# If not already present, create the sequential directory
if [ ! -d "$SequentialDIR" ]; then
    mkdir "$SequentialDIR"
fi

