file="$TESTSDIR/test1.txt"
# Create the test files
for i in `seq 1 8`
do
    # If file not present, create the test  
    if [ ! -f "$TESTSDIR/test$i.txt" ]; 
    then
        touch "$TESTSDIR/test$i.txt"

        max=5
        if [ $i -eq 4 ]; 
        then
            max=4
        fi
        # if i greater than 5 then max is 2
        if [ $i -gt 5 ]; 
        then
            max=2
        fi
        if [ ! $i -eq 1 ]; 
        then
            # Copy the previous file to the current file 2 times to increase the file size
            for j in `seq 1 $max`
            do
                cat "$TESTSDIR/test$(($i-1)).txt" >> "$TESTSDIR/test$i.txt"
            done
        else
            # Write the lorem ipsum text to the file i x 1000 times
            for j in `seq 1 $((i*80))`
            do
                echo "$LoremIpsum" >> "$TESTSDIR/test$i.txt"
            done
        fi
        echo "test$i.txt created"
    fi
done